import axios from 'axios';

const apiUrl = "http://localhost:5000"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/Items`)    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
      try {
        const result = await axios.post(`${apiUrl}/Items`, {name});
        return result.data; // מחזיר את הנתונים שהתקבלו מהשרת
      } catch (error) {
        console.error('Error adding task:', error);
        throw error; // ניתן להשליך שגיאה במקרה של כישלון
      }
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    try {
      const result = await axios.put(`${apiUrl}/Items/${id}`, { isComplete });
      return result.data; // מחזיר את הנתונים שהתקבלו מהשרת
    } catch (error) {
      console.error('Error updating task:', error);
      throw error; // ניתן להשליך שגיאה במקרה של כישלון
    }
  },

  deleteTask:async(id)=>{
    console.log('deleteTask')
    try {
      await axios.delete(`${apiUrl}/Items/${id}`);
      return { success: true }; // מחזיר אובייקט שמעיד על הצלחה
    } catch (error) {
      console.error('Error deleting task:', error);
      throw error; // ניתן להשליך שגיאה במקרה של כישלון
    }
  }
};
